.sp-gas {
  display: flex;
  position: absolute;
  bottom: 0;
  align-items: center;
  padding: 10px;
  background: rgba(0, 0, 0, 0.3);
  justify-content: space-between;
  width: 74px;
}

.sp-gas-num {
  margin-left: 5px;
  color: #ddd;
  font-size: 14px;
}

